function changeLanguage(lang) {
    localStorage.setItem('language', lang);
    updateContent(lang);
}

function updateContent(lang) {
    // Fallback to English if the selected language is missing translations
    const translation = translations[lang] && Object.keys(translations[lang]).length > 0 ? translations[lang] : translations['en'];

    document.querySelectorAll('[data-i18n]').forEach(element => {
        const key = element.getAttribute('data-i18n');
        if (translation[key]) {
            // Handle elements with HTML content (e.g., links)
            if (element.innerHTML.includes('<a')) {
                element.innerHTML = translation[key];
            } else {
                element.textContent = translation[key];
            }
        } else {
            // Fallback to English if translation for the key is missing
            if (translations['en'][key]) {
                if (element.innerHTML.includes('<a')) {
                    element.innerHTML = translations['en'][key];
                } else {
                    element.textContent = translations['en'][key];
                }
            }
        }
    });
    document.documentElement.lang = lang;
    document.title = translation.title || translations['en'].title || 'Dripredgames';
}

function initializeLanguage() {
    const savedLang = localStorage.getItem('language') || 'en';
    document.getElementById('language-select').value = savedLang;
    updateContent(savedLang);
}